using System.Collections.Generic;
using System.Linq;
using NUnit.Framework;
using RepoCat.Persistence.Models;
using RepoCat.RepositoryManagement.Service;

namespace RepoCat.Tests
{

    [TestFixture]
    public class SearchStringTokenizerTests
    {
        [Test]
        public void SplitTypicalProjectNameString_NoPascalCase()
        {
            var result = SearchInputTokenizer.Tokenize("Microsoft.NETCore.App.dll");

            Assert.AreEqual("Microsoft", result[0]);
            Assert.AreEqual("NETCore", result[1]);

            Assert.AreEqual(2, result.Count);

        }

        [Test]
        public void SplitTypicalProjectNameString_WithPascalCase()
        {
            var result = SearchInputTokenizer.Tokenize("Microsoft.NETCore.MultiPurposeTool");

            Assert.AreEqual("Microsoft", result[0]);
            Assert.AreEqual("NETCore", result[1]);
            Assert.AreEqual("Multi", result[2]);
            Assert.AreEqual("Purpose", result[3]);
            Assert.AreEqual("Tool", result[4]);

            Assert.AreEqual(5, result.Count);

        }

        [Test]
        public void SplitLongerStringMix_WithPascalCase()
        {
            var result = SearchInputTokenizer.Tokenize("SomePascalCase along with someCamelCase. Also-a sentence,with poor punctuation!Lovely, right?");

            Assert.AreEqual("Some", result[0]);
            Assert.AreEqual("Pascal", result[1]);
            Assert.AreEqual("Case", result[2]);
            Assert.AreEqual("along", result[3]);
            Assert.AreEqual("with", result[4]);
            Assert.AreEqual("some", result[5]);
            Assert.AreEqual("Camel", result[6]);
            Assert.AreEqual("Case", result[7]);
            Assert.AreEqual("Also", result[8]);
            Assert.AreEqual("sentence", result[9]);
            Assert.AreEqual("with", result[10]);
            Assert.AreEqual("poor", result[11]);
            Assert.AreEqual("punctuation", result[12]);
            Assert.AreEqual("Lovely", result[13]);
            Assert.AreEqual("right", result[14]);

            Assert.AreEqual(15, result.Count);

        }

